import React, { useState, useContext } from "react";
import { LoginContext } from "../Context/LoginContext";
import "../App.css";
import logo from "../assets/HireMe.png";


export default function Login() {

    const {setUserEmail} = useContext(LoginContext)



    const [emailForm, setEmailForm] = useState('')
    const [passwordForm, setPasswordForm] = useState('')


    const handleInputChange = (event) => {
        const inputName = event.target.name;
        const inputValue = event.target.value


        if (inputName === 'email') {
            setEmailForm(inputValue)
            console.log(inputValue)
        } else if (inputName === 'password') {
            setPasswordForm(inputValue)
            console.log(inputValue)
        }
    }

 const handleSubmit = (event) => {
        const validEmailRegex = new RegExp(/^([a-z0-9_\.-]+)@([\da-z\.-]+)\.([a-z\.]{2,6})$/)

        const emailCheck = validEmailRegex.test(emailForm)
        if (emailCheck === false) {
          window.alert('Not a valid email address')
        }
        if ( !emailForm || !passwordForm) {
          window.alert('All three fields must be complete.')
        }
        event.preventDefault()

        if (emailCheck === true && emailForm && passwordForm) {
           window.alert('Logging in Test')

        setEmailForm('')
        setPasswordForm('')
        }
      }

    return (
        <>
        <header className="header">
          {/* Logo and dropdown menu */}
          <div className="logo">Your Logo</div>
          <div className="dropdown">
            <button className="dropbtn">Menu</button>
            <div className="dropdown-content">
              <a href="#">Candidate Login</a>
              <a href="#">Employer Login</a>
              <a href="#">Register Now</a>
            </div>
          </div>
          {/* Slogan */}
          <p className="slogan">Your Slogan Goes Here</p>
        </header>

        <main className="main">
          <section className="login-section">
            {/* Login form */}
            <form className="login-form">
              {/* Email input */}
              <label className="form-label" htmlFor="email">
                Email:
                <input
                  className="form-box"
                  type="text"
                  name="email"
                  value={emailForm || ""}
                  onChange={handleInputChange}
                />
              </label>

              {/* Password input */}
              <label className="form-label" htmlFor="password">
                Password :
                <input
                  className="form-box"
                  type="password" /* Use type 'password' for password fields */
                  name="password"
                  value={passwordForm || ""}
                  onChange={handleInputChange}
                />
              </label>

              {/* Submit button */}
              <input
                className="btn"
                type="submit"
                value="Send"
                onClick={handleSubmit}
              />
            </form>
          </section>

          {/* Two columns with images and paragraphs */}
          <section className="columns">
            <div className="column">
              <img src="your-image-url-1.jpg" alt="Image 1" />
              <p>Your text here</p>
            </div>
            <div className="column">
              <img src="your-image-url-2.jpg" alt="Image 2" />
              <p>Your text here</p>
            </div>
          </section>
        </main>
      </>
    );
  }
